import React from 'react';
import axios from 'axios';

import withAnalytcs from '../src/hocs/withAnalytcs';

const Detail = ({ user }) => (
  <div>
    <h1>{user.login}</h1>
    <img src={user.avatar_url} width="200" />
  </div>
);

Detail.getInitialProps = async () => {
  const response = await axios.get('https://api.github.com/users/brunodiego5');

  console.log(response);

  return { user: response.data }
};

export default withAnalytcs()(Detail);
